updates.updatestats:
function(doc, req) {
       if (!doc || doc.type !== 'boardgame') {
         return [null, 'No doc found'];
       }
       var stats = JSON.parse(req.body);
       var g = doc.geeklists.filter(function(e){ return e.objectid === stats.geeklistid});
       g[0].latest = stats;
       return [doc, 'Document Updated'];
     }
