geeklist.map:
function(doc) {
  if(doc.geeklists.length > 0){
	for(idx = 0; idx < doc.geeklists.length; idx++){
      	  emit(doc.geeklists[idx].objectid, null);
	}
  }
}

geeklist_by_crets.map:
function(doc) {
  if(doc.geeklists.length > 0){
	for(idx = 0; idx < doc.geeklists.length; idx++){
      	  emit([doc.geeklists[idx].objectid, Date.parse(doc.geeklists[idx].crets)], null);
	}
  }
}

geeklist_by_thumbs.map:
function(doc) {
  if(doc.geeklists.length > 0){
	for(idx = 0; idx < doc.geeklists.length; idx++){
      	  emit([doc.geeklists[idx].objectid, parseInt(doc.geeklists[idx].latest.thumbs)], null);
	}
  }
}

geeklist_by_name.map:
function(doc) {
  if(doc.geeklists.length > 0){
	var name = doc.name.filter(function(e){return e.primary == "true"})[0].name;
	for(idx = 0; idx < doc.geeklists.length; idx++){
      	  emit([doc.geeklists[idx].objectid, name], null);
	}
  }
}

